        [Theory]
        [InlineData("(())", 0)]
        [InlineData("()()", 0)]
        [InlineData("(((", 3)]
        [InlineData("(()(()(", 3)]
        [InlineData("))(((((", 3)]
        [InlineData("())", -1)]
        [InlineData("))(", -1)]
        [InlineData(")))", -3)]
        [InlineData(")())())", -3)]
        public void TestPuzzle(string floor, int expected)
        {
            var sut = new Parser();
            Assert.Equal(expected, sut.Parse(floor));

        }

        [Fact]
        public void TestPuzzleFindFloor()
        {
            var sut = new Parser();
            var answer = sut.Parse(InputPuzzle);
            Assert.Equal(138, answer);
        }

        [Fact]
        public void TestPuzzleFindPositionOfFirstBasement()
        {
            var position = FindPositionOfFirstBasement(Parser.Map(InputPuzzle));
            Assert.Equal(1771, position);
        }

        private static int FindPositionOfFirstBasement(IEnumerable<int> moves)
        {
            var position = 0;
            var actualFloor = 0;
            var basement = -1;
            foreach (var move in moves)
            {
                position++;
                actualFloor += move;
                if (actualFloor == basement)
                {
                    return position;
                }
            }

            return -1;
        }
    }

    public class Parser
    {
        public int Parse(string floor)
        {
            return Map(floor)
                .Sum();
        }

        public static IEnumerable<int> Map(string floor)
        {
            return floor.Select(x => x.Equals('(') ? 1 : -1);
        }
    }
